import { Container, Icon, Grid } from '@material-ui/core';
import CopyrightIcon from '@material-ui/icons/Copyright';
import React, { Component, useState } from 'react';
import IconCpf from '@material-ui/icons/PermIdentity';
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';
import { InputGroup, InputGroupAddon, InputGroupText } from 'reactstrap';
import { Collapse, Navbar, NavbarToggler, NavbarBrand, Nav, NavItem, NavLink, UncontrolledDropdown, DropdownToggle, DropdownMenu, DropdownItem, NavbarText } from 'reactstrap';
import logoLogin from "./desenho/Logo.png"
import iconLogin from "./desenho/iconMembroLogin.png"
import './login_membro_css/membro.css';
import IconeConSenha from '@material-ui/icons/Lock';
import ApiService from "../../ApiService";

class Login_membro extends Component {

  acendericone(i, inb) {
    document.getElementById(i).style.color = "#061C99";
    document.getElementById(inb).style.borderColor = "#061C99";

  }

  apagaricone(i, inb) {
    document.getElementById(i).style.color = "#aaa";
    document.getElementById(inb).style.borderColor = "#aaa";
  }

  constructor(props) {
    super(props);
    this.state = {
      email: '',
      senha: ''
    }
  }

  loginMembro() {
    e.preventDefault();
    let membro = {email: document.getElementById("cpf_login").value, senha:document.getElementById("senha_login").value }
    ApiService.loginMembro(membro)
  }

  render() {
    return (

      <div className="col-xs-12 col-md-12 col-sm-12 col-lg-12">

        <div className="containerHeader space-between">
          <nav className="navbar fixed-top navbar-light bg-light">
            <div className="container">
              <a className="my-0 mr-md-auto font-weight-normal">
                <img style={{ width: "120px" }} id="logo_login" src={logoLogin}></img>
              </a>
              <a className="btn " href="#">Inicio</a>
              <a className="btn " href="#">Cadastro</a>
            </div>
          </nav>
        </div>




        <Container maxWidth="sm">
          <div >
            <Form >
              <Grid spacing={1}>
                <Grid item xs={12}>
                  <img src={iconLogin} id="icon_login"></img>
                  <Label className="Label_login"><b>Login Membro</b></Label>
                </Grid>
                <Grid item xs={12}>
                  <FormGroup className="col-xs-10 col-md-12 col-sm-12 col-lg-12">
                    <InputGroup >
                      <InputGroupAddon addonType="prepend">
                        <InputGroupText className="esc">
                          <i><IconCpf id="iconCpfLogin" /></i>
                        </InputGroupText>
                      </InputGroupAddon>
                      <Input type="text" name="cpf_login" id="cpf_login" value={this.state.email} placeholder="Digite Seu CPF"
                        onFocus={() => this.acendericone("iconCpfLogin", "cpf_login")}
                        onBlur={() => this.apagaricone("iconCpfLogin", "cpf_login")}></Input>
                    </InputGroup>
                  </FormGroup>
                </Grid>
                <Grid item xs={12} >
                  <FormGroup className="col-xs-12 col-md-12 col-sm-12 col-lg-12">
                    <InputGroup>
                      <InputGroupAddon addonType="prepend">
                        <InputGroupText className="esc">
                          <i><IconeConSenha id="icon_senha_login" /></i>
                        </InputGroupText>
                      </InputGroupAddon>
                      <Input type="text" name="senha_login" id="senha_login" placeholder="Digite Sua Senha"
                        onFocus={() => this.acendericone("icon_senha_login", "senha_login")}
                        onBlur={() => this.apagaricone("icon_senha_login", "senha_login")}  value={this.state.senha}></Input>
                    </InputGroup>
                  </FormGroup>
                </Grid>
                <Grid item xs={12}>
                  <a href="http://localhost:3000/RecuperarSenha" className="login_recuperar_senha mt-1"><i>Esqueceu sua senha</i></a>
                </Grid>
                <Grid >
                  <Button id="button_login" onClick={ this.loginMembro} outline color="primary">Acessar</Button>
                </Grid>
                <Grid item xs={12} className="center mt-3">
                  <p id="p_login">OU</p>
                </Grid>
                <Grid className="center " item xs={12}>
                  <Button id="button_login2" outline color="primary">Criar conta de acesso</Button>
                </Grid>
              </Grid>
            </Form>

          </div>

        </Container>

      {/**  Footer  **/}
        <div className="containerHeader container space-between">
          <nav className="navbar fixed-bottom navbar-light bg-light">
            <div className="container">
              <p></p>
              <p style={{ fontSize: "12px" }} className="center rodape">2020-2021-Central Cidadã |Todos os Direitos Reservados É proibido a reprodução total ou parcil de qualquer conteúdo deste site.</p>
              <p></p>
            </div>
          </nav>
        </div>
      </div>

    );
  }
}

export default Login_membro;
