import CopyrightIcon from '@material-ui/icons/Copyright';
import React, { Component, useState } from 'react';
import IconCpf from '@material-ui/icons/PermIdentity';
import { InputGroup, InputGroupAddon, InputGroupText } from 'reactstrap';
import { Collapse, Navbar, NavbarToggler, NavbarBrand, Nav, NavItem, NavLink, UncontrolledDropdown, DropdownToggle, DropdownMenu, DropdownItem, NavbarText } from 'reactstrap';
import logoLogin from "./desenho/Logo.png"
import iconLogin from "./desenho/icone.png"
import './login_adm_css/login_adm.css';
import { TextField, Grid, Imagem, Button, View } from '@material-ui/core/';

class Login_adm extends Component {

    //definição de props e statas
    constructor(props) {
        super(props);
        this.state = {
            id: '',
            nome: '',
            endereco: '',
            cpf: '',
            nascimento: '',
            admissao: '',
            salario: ''
        }
        this.saveUser = this.saveUser.bind(this);
    }
    //salva	o	usuario no	backend
    saveUser = (e) => {
        //TODO:	obtem os	dados	do	state	e	envia	para	o	backend salvar	os	dados


        e.preventDefault();
        let usuario = {
            id: this.state.id, nome: this.state.nome, endereco: this.state.endereco, cpf: this.state.cpf,
            nascimento: this.state.nascimento, admissao: this.state.admissao
        };
        ApiService.salvarUsuario(usuario)
            .then(res => {
                this.props.history.push('/');
                alert('Usuario salvo com sucesso!');
            })
            .catch(err => {
                alert('Não foi possível salvar o usuario!!!!');
            });
    }

    onChange = (e) =>
        this.setState({ [e.target.name]: e.target.value });


    render() {
        return (
            <Container maxWidth="sm">
                <div className='espaco'>
                    <h2 className="text-center">Adicionar Funcionário</h2>
                    <form> <Grid container spacing={3}>
                        <Grid item xs={12}>

                        </Grid>
                        <Grid item xs={12}>

                        </Grid>
                        <Grid item xs={12}>

                        </Grid>
                        <Grid item xs={6}>

                        </Grid>
                        <Grid item xs={6}>

                        </Grid>
                        <Grid item xs={6}>

                        </Grid>


                    </Grid>
                        <br></br>
                        <Button variant="contained" color="primary" disableElevation onClick={(e) => { this.saveUser(e) }}>
                            Salvar
                                        </Button>
                    </form>
                </div>
            </Container>
        );
    }
}

export default Login_adm;
